mapper.select:
SELECT content, who, id, score
FROM (
         SELECT content, who, id, idx, score
         FROM QUERYHISTORY AS q
         WHERE paperId = #{paperIsd} AND username = #{username}
         ORDER BY idx DESC
             LIMIT 10
     ) AS subquery
ORDER BY idx ASC;

SELECT content, who, id, score
FROM QUERYHISTORY
WHERE id = #{id} AND username = #{username};

mapper.update:
update QUERYHISTORY
set score = #{score}
where id = #{id};
