mapper.update:
update PAPER
set cites=#{updateParam.cites},
    likes=#{updateParam.likes},
    summary = #{updateParam.summary}
where paperId = #{id}

update PAPER
set summary = #{summary}
where paperId = #{id}

mapper.select:
select paperId,title, conference, cites, url, summary, likes, year
from PAPER
where paperId = #{id}

select paperId,title, conference, cites, url, summary, likes, year
from PAPER
where paperId = #{id}

select summary
from PAPER
where paperId = #{id}
