mapper.insert:
insert into ITEMANNOTATION (paperId, itemId, workspaceId, itemType, text, color, comment, pageLabel, positionString, dateCreated, dateModified)
values (#{paperId}, #{itemId}, #{workspaceId}, #{itemType}, #{text}, #{color}, #{comment}, #{pageLabel}, #{positionString}, #{dateCreated}, #{dateModified})


    SELECT LAST_INSERT_ID();

mapper.select:
select *
from ITEMANNOTATION
where paperId = #{paperId} and workspaceId = #{workspaceId}

select *
from ITEMANNOTATION
where itemId = #{itemId} AND workspaceId = #{workspaceId} AND paperId = #{paperId};
